configfiles.configfile:
graph_type = "${graph_type}"
globalgraph = function () { ## Setup R error handling to go to stderr options( show.error.messages=F, error = function () { cat( geterrmessage(), file=stderr() ); q( "no", 1, F ) } ) signature = read.delim("${output}", header=TRUE) ## Open output1 PDF file pdf( "${output1}" ) signaturez=(signature[,2] -mean(signature[,2]))/sd(signature[,2]) plot(signaturez, type = "b", main="signature", cex.main=2, xlab="overlap (nt)", ylab="z-score", pch=19, cex=0.8, col="darkslateblue", lwd=3, cex.lab=1.5, cex.axis=1.4, xaxt="n") axis(1, at=seq(from=1, to=length(signature[,1]), by=3) ) devname = dev.off() ## Close the PDF file ## Open output2 PDF file pdf( "${output2}" ) YLIM=max(signature[,2]) plot(signature[,1:2], type = "h", xlab="overlap (nt)", ylim=c(0,YLIM), ylab="number of pairs", col="darkslateblue", lwd=7) devname = dev.off() ## Close the PDF file ## Open output3 PDF file pdf( "${output3}" ) plot(signature[,1], signature[,3]*100, type = "l", main="ping-pong Signature of 23-29 against 23-29nt small RNAs", cex.main=1, xlab="overlap (nt)", ylab="ping-pong signal [%]", ylim=c(0,50), pch=19, col="darkslateblue", lwd =4, cex.lab=1.2, cex.axis=1, xaxt="n") axis(1, at=seq(from=1, to=length(signature[,1]), by=3) ) devname = dev.off() ## Close the PDF file }
treillisgraph = function () { ## Open output3 PDF file pdf( "${output3}", paper="special", height=11.69, width=8.2677 ) signature = read.delim("${output}", header=TRUE) options( show.error.messages=F, error = function () { cat( geterrmessage(), file=stderr() ); q( "no", 1, F ) } ) library(lattice) print (xyplot(signature[,3]*100~signature[,1]|signature[,4], type = "l", xlim=c(1,26), main="ping-pong Signature of 23-29 against 23-29nt small RNAs", par.strip.text=list(cex=.5), strip=strip.custom(which.given=1, bg="lightblue"), scales=list(cex=0.5), cex.main=1, cex=.5, xlab="overlap (nt)", ylab="ping-pong signal [%]", pch=19, col="darkslateblue", lwd =1.5, cex.lab=1.2, cex.axis=1.2, layout=c(4,12), as.table=TRUE, newpage = T) ) devnname = dev.off() }
if (graph_type=="global") { globalgraph()
} if(graph_type=="lattice") { treillisgraph() }
